public class Main {
    public static void main(String[] args) {
        String message = "we found a treasure!";
        for(char ch: message.toCharArray()) {
            if(Character.isAlphabetic(ch)) {
                System.out.print((char)(25 - (ch - 'a') + 'a'));
            } else {
                System.out.print(ch);
            }
        }
    }
}

